function getMp4DurationFromBase64(base64) {
    const buffer = Buffer.from(base64, "base64");

    let offset = 0;

    while (offset < buffer.length) {
        const size = buffer.readUInt32BE(offset);
        const type = buffer.toString("ascii", offset + 4, offset + 8);

        // Procurar o box 'moov'
        if (type === "moov") {
            let moovOffset = offset + 8;
            const moovEnd = offset + size;

            while (moovOffset < moovEnd) {
                const boxSize = buffer.readUInt32BE(moovOffset);
                const boxType = buffer.toString("ascii", moovOffset + 4, moovOffset + 8);

                // Procurar 'mvhd'
                if (boxType === "mvhd") {
                    const version = buffer[moovOffset + 8];

                    let timescale, duration;

                    if (version === 0) {
                        timescale = buffer.readUInt32BE(moovOffset + 20);
                        duration = buffer.readUInt32BE(moovOffset + 24);
                    } else {
                        timescale = buffer.readUInt32BE(moovOffset + 28);
                        duration = Number(buffer.readBigUInt64BE(moovOffset + 32));
                    }

                    return Number(duration) / timescale;
                }

                moovOffset += boxSize;
            }
        }

        offset += size;
    }

    throw new Error("mvhd not found in MP4");
}

